import { styled } from "styled-components";

export const ErrorMainCont = styled.div``;

export const ErrorCont = styled.div``;

export const ErrorTitleCont = styled.div`
  display: flex;
  align-items: center;
  gap: 12px;
  padding: 80px 0 0 135px;
  margin-bottom: 140px;
`;

export const ErrorTitleContDesc = styled.p`
  opacity: 0.5;
  color: #000;
  font-family: "Poppins";
  font-size: 14px;
  font-style: normal;
  font-weight: 400;
  line-height: 21px;
`;

export const ErrorTitleContTxt = styled.p`
  font-family: "Poppins";
  font-size: 14px;
  color: #000;
  font-style: normal;
  font-weight: 400;
  line-height: 21px;
`;

export const ErrorPageCont = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
`;

export const ErrorPageTitle = styled.p`
  font-family: "Inter";
  font-size: 110px;
  font-style: normal;
  font-weight: 500;
  line-height: 115px;
  letter-spacing: 3.3px;
  color: #000;
  margin-bottom: 40px;
`;

export const ErrorPageTitleDesc = styled.p`
  font-family: "Poppins";
  font-size: 16px;
  font-style: normal;
  font-weight: 400;
  line-height: 24px;
  color: #000;
  margin-bottom: 80px;
`;

export const ErrorPageTitleBtn = styled.button`
  padding: 16px 48px;
  background-color: #db4444;
  color: #fafafa;
  font-family: "Poppins";
  font-size: 16px;
  font-style: normal;
  font-weight: 500;
  line-height: 24px;
  border: none;
  border-radius: 4px;
  margin-bottom: 140px;
`;
