import React, { useState } from 'react'
import { NavLink } from 'react-router-dom'
import cl from './style.module.css'
import djostik1 from '../images/djostik1.png'
import djostik2 from '../images/djostik2.png'
import djostik3 from '../images/djostik3.png'
import djostik4 from '../images/djostik4.png'
import djostik5 from '../images/djostik5.png'
import { ReactComponent as Sart } from '../images/star.svg'
import { ReactComponent as EmptyStar } from '../images/emptystar.svg'
import { ReactComponent as YarimStar } from '../images/yarimYulduz.svg'
import { ReactComponent as BlueCircle } from '../images/bluecircle.svg'
import { ReactComponent as RedCircle } from '../images/crimsoncircle.svg'
import { ReactComponent as Minus } from '../images/minus.svg'
import { ReactComponent as Plus } from '../images/plus.svg'
import { ReactComponent as WishHeart } from '../images/wishheart.svg'
import { ReactComponent as Delivery } from '../images/delivery.svg'
import { ReactComponent as Return } from '../images/return.svg'
import gamepad from '../images/redgamepad.png'
import keyboard from '../images/keyboard.png'
import kalonka from '../images/kalonka.png'
import manitor from '../images/monitor.png'
import { ReactComponent as Category } from '../images/category.svg'
import { ReactComponent as Heart } from '../images/heart.svg'
import { ReactComponent as Eye } from '../images/eye.svg'
export default function DetailsPage() {
    const [itemCount, setItemCount] = useState(0)
    const [otherItemDatas, setotherItemDatas] = useState([
        // { id: 1, src: gamepad, discount: 40, itemName: 'HAVIT HV-G92 Gamepad', realPrice: 160, rating: 5, reviews: 88 },
        // { id: 2, src: keyboard, discount: 35, itemName: 'AK-900 Wired Keyboard', realPrice: 1160, rating: 4, reviews: 75 },
        { id: 3, src: manitor, discount: 30, itemName: 'IPS LCD Gaming Monitor', realPrice: 400, rating: 3, reviews: 99 },
        { id: 4, src: kalonka, discount: 30, itemName: 'RGB liquid CPU Cooler', realPrice: 170, rating: 4.3, reviews: 65 }
    ])

    const [detailsImg, setdetailsImg] = useState([djostik1, djostik2, djostik3, djostik4, djostik5])
    const [itemDetails, setItemDetails] = useState([
        // { id: 1, itemName: 'Havic HV G-92 Gamepad', rating: 4, reviews: 150, instock: true, price: 192.00, aboutItem: 'PlayStation 5 Controller Skin High quality vinyl with air channel adhesive for easy bubble free install & mess free removal Pressure sensitive.', colours: ['blue', 'red'], sizes: ['xs', 's', 'm', 'l', 'xl'] }
    ])

    function getrating(num) {
        // console.log(num);
        const starList = []
        const emptyStarList = []
        const chalaStar = []
        let son;
        for (let i = 1; i <= num; i++) {
            son = 5 - num
            starList.push(<Sart />)
        }
        if (son - Math.floor(son) < 1) {
            chalaStar.push(<YarimStar />)
        } else {
            for (let i = 1; i <= son;i++) {
                emptyStarList.push(<EmptyStar />)
            }
        }
        // for (let i = 1; i <= 5 - num; i++) {
        //     son = Math.abs((5 - num) - i)
        //     emptyStarList.push(<EmptyStar />)
        // }
        // if (son >= 0.5 ) {
        //     fullOrEmotyStars.push(<YarimStar />)
        // } else {
        //     fullOrEmotyStars.push(<EmptyStar />)
        // }
        return starList.concat(chalaStar, emptyStarList)
    }


    function getColours(coloursArr) {
        const colours = []
        const filteredColoursList = []
        for (let item of coloursArr) {
            if (!filteredColoursList.includes(item)) {
                filteredColoursList.push(item)
            }
        }
        for (let item of filteredColoursList) {
            colours.push(<div className={cl.colorBoxBorder} key={item}><div className={cl.colorBox} style={{ background: `${item}` }}></div></div>)
        }
        return colours
    }

    function addItem() {
        setItemCount(prev => prev + 1)
    }

    function minusItem() {
        if (itemCount > 0) {
            setItemCount(prev => prev - 1)
        }
    }

    function addWishList() {
        document.getElementById('wishHeart').classList.toggle('qushildi')
    }

    function changeImg(num) {
        setdetailsImg(prev => [...prev, prev[4] = prev[num]])
    }
    // console.log(detailsImg);

    return (
        <>
            <div className={cl.detailsWrapper}>
                <div className="container">
                    <p className={cl.detailsLinks}>
                        <NavLink to={'acount'}>Acount</NavLink> / <NavLink to={'gaming'}>Gaming</NavLink> / <NavLink to={'/'}>Havic HV G-92 Gamepad</NavLink>
                    </p>
                    {/* mabodo uxshamsa */}
                    <div className={cl.itemDetailsBox}>
                        <div className={cl.itemDetailsImgBox}>
                            {
                                <>
                                    <div className={cl.itemSmallImgBoxsWrapper}>
                                        <div className={cl.itemSmallImgBox}>
                                            <img onClick={() => changeImg(0)} src={detailsImg[0]} alt="" />
                                        </div>
                                        <div className={cl.itemSmallImgBox}>
                                            <img onClick={() => changeImg(1)} src={detailsImg[1]} alt="" />
                                        </div>
                                        <div className={cl.itemSmallImgBox}>
                                            <img onClick={() => changeImg(2)} src={detailsImg[2]} alt="" />
                                        </div>
                                        <div className={cl.itemSmallImgBox}>
                                            <img onClick={() => changeImg(3)} src={detailsImg[3]} alt="" />
                                        </div>
                                    </div>
                                    <div className={cl.itemBigImgBox}>
                                        <img onClick={() => changeImg(4)} src={detailsImg[4]} alt="" />
                                    </div>
                                </>
                            }
                        </div>

                        <div>
                            {
                                itemDetails.map(i =>
                                    <>
                                        <div key={i.id}>
                                            <h1 className={cl.itemName}>{i.itemName}</h1>
                                            <div className={cl.usersDataWrapper}>
                                                <div className={cl.starsWrapper}>
                                                    {
                                                        getrating(i.rating)
                                                    }
                                                </div>
                                                <pre className={cl.userReviewsText}>({i.reviews} Reviews)  |  <span>{i.instock ? 'In Stock' : 'qomadi'}</span></pre>
                                            </div>
                                        </div>
                                        <h1 className={cl.itemPrice}>${i.price}</h1>
                                        <p className={cl.aboutItem}>{i.aboutItem}</p>
                                        <hr />

                                        <div className={cl.coloursWrapper}>
                                            <h1 className={cl.colourstitle}>Colours:</h1>
                                            <div className={cl.colorBoxWrapper}>
                                                {
                                                    getColours(i.colours)
                                                }
                                            </div>
                                        </div>

                                        <div className={cl.sizesBigWrapper}>
                                            <h1 className={cl.colourstitle}>Size:</h1>
                                            <div className={cl.sizesSmallWrapper}>
                                                {
                                                    i.sizes.map(item =>
                                                        <div className={cl.sizeBox}>
                                                            <p className={cl.sizeText}>{item}</p>
                                                        </div>
                                                    )
                                                }
                                            </div>
                                        </div>
                                    </>
                                )
                            }

                            <div className={cl.salesbox}>
                                <div className={cl.counterBox}>
                                    <button onClick={minusItem} className={cl.minusCountBtn}><Minus /></button>
                                    <div className={cl.countBox}>
                                        <h1 className={cl.countText}>{itemCount}</h1>
                                    </div>
                                    <button onClick={addItem} className={cl.plusCountBtn}><Plus /></button>
                                </div>

                                <button className={cl.buyBtn}>Buy Now</button>

                                <button onClick={addWishList} className={cl.wishHeartBtn}><WishHeart id='wishHeart' /></button>
                            </div>

                            <div className={cl.opportunitywrapper}>
                                <div className={cl.opportunityBox} >
                                    <Delivery />
                                    <div>
                                        <h3 className={cl.opportunityTitle}>Free Delivery</h3>
                                        <p className={`${cl.opportunityText} ${cl.deliveryText}`}>Enter your postal code for Delivery Availability</p>
                                    </div>
                                </div>
                                <hr />
                                <div className={cl.opportunityBox} >
                                    <Return />
                                    <div>
                                        <h3 className={cl.opportunityTitle}>Return Delivery</h3>
                                        <p className={cl.opportunityText}>Free 30 Days Delivery Returns. Details</p>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                    <div className={cl.otherItemsTitleWrapper}>
                        <Category />
                        <h1 className={cl.otherItemsTitle}>Related Item</h1>
                    </div>

                    <div className={cl.otherItemsBox}>
                        {
                            otherItemDatas.map(item =>
                                <div className={cl.itembox} key={item.id}>
                                    <div style={{ backgroundImage: `url(${item.src})` }} className={cl.itemImgBox}>
                                        <div className={cl.discountBox}>
                                            <p className={cl.discountText}>-{item.discount}%</p>
                                        </div>
                                        <div className={cl.actionButtonsBox}>
                                            <button className={cl.actionBtn}><Heart /></button>
                                            <button className={cl.actionBtn}><Eye /></button>
                                        </div>
                                        <button className={cl.addToCardBtn}>Add To Cart</button>
                                    </div>
                                    <h3 className={cl.otheritemName}>{item.itemName}</h3>
                                    <div className={cl.pricesWrapper}>
                                        {
                                            item.discount > 0 ?
                                                <div className={cl.realAndDiscountPriceBox}>
                                                    <p style={{ color: '#DB4444', opacity: '1' }} className={cl.prices}>${item.realPrice - (item.realPrice / 100) * item.discount}</p>
                                                    <del className={cl.prices}>${item.realPrice}</del>
                                                </div>
                                                :
                                                <p className={cl.prices}>${item.realPrice}</p>
                                        }
                                    </div>

                                    <div className={cl.ratingBox}>
                                        <div className={cl.itemsRatingBox}>
                                            {
                                                getrating(item.rating)
                                            }
                                        </div>
                                        <p className={cl.itemsreviews}>({item.reviews})</p>
                                    </div>
                                </div>
                            )
                        }
                    </div>
                </div>
            </div>
        </>
    )
}
