import { styled } from "styled-components";

export const DetailsWrapper = styled.div`
    padding: 80px 0 140px;
`

export const DetailsLinksBox = styled.div`
    display: flex;
    align-items: center;
    margin-bottom: 80px;
`

export const DetailsLinks = styled.pre`
    font-size: 14px;
    line-height: 21px;
    opacity: .5;
    &>a{
        color: black;
        text-decoration: none;
    }
    &> a.active{
        opacity: 1;
        color: red;
    }
`
