import { styled } from "styled-components";

export const DetailsWrapper = styled.div`
    padding: 80px 0 140px;
`

export const DetailsLinksBox = styled.div`
    display: flex;
    align-items: center;
    gap: 4px;
    margin-bottom: 80px;
`

export const DetailsLinks = styled.p`
    font-size: 14px;
    line-height: 21px;
    opacity: .5;
    &>a{
        color: black;
        text-decoration: none;
    }
    &:has(a.active){
        opacity: 1;
    }
`

export const ItemDetailsBox = styled.div`
    display: flex;
    gap: 70px;
`

export const ItemDetailsImgBox = styled.div`
    display: flex;
    gap: 30px;
`

export const ItemSmallImgBoxsWrapper = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: space-between;
`

export const ItemSmallImgBox = styled.div`
    display: flex;
    width: 170px;
    height: 138px;
    justify-content: center;
    align-items: center;
    border-radius: 4px;
    background: #F5F5F5;
`

export const ItemBigImgBox = styled.div`
    width: 500px;
    background: #F5F5F5;
    border-radius: 4px;
    display: flex;
    justify-content: center;
    align-items: center;
`

export const ItemName = styled.h1`
    font-family: Inter;
    font-size: 24px;
    font-weight: 600;
    line-height: 24px;
    letter-spacing: 0.72px;
    margin-bottom: 16px;
`

export const UsersDataWrapper = styled.div`
    color: white;
`
export const nimadir = styled.p`
    color: white;
`
