import { styled } from "styled-components";

export const DetailsWrapper = styled.div`
    padding: 80px 0 140px;
`

export const DetailsLinksBox = styled.div`
    display: flex;
    align-items: center;
    gap: 4px;
    margin-bottom: 80px;
`

export const DetailsLinks = styled.p`
    font-size: 14px;
    line-height: 21px;
    opacity: .5;
    &>a{
        color: black;
        text-decoration: none;
    }
    &:has(a.active){
        opacity: 1;
    }
`

export const ItemDetailsBox = styled.div`
    display: flex;
    gap: 70px;
`

export const ItemDetailsImgBox = styled.div`
    display: flex;
    gap: 30px;
`

export const ItemSmallImgBoxsWrapper = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: space-between;
`
export const ItemSmallImgBox = styled.div`
    display: flex;
    width: 170px;
    height: 138px;
    justify-content: center;
    align-items: center;
    border-radius: 4px;
    background: #F5F5F5;
    `
