import { styled } from "styled-components";

export const DetailsWrapper = styled.div`
    padding: 80px 0 140px;
`

export const DetailsLinks = styled.p`
    font-size: 14px;
    line-height: 21px;
    opacity: .5;
    margin-bottom: 80px;
    &>a{
    color: black;
    text-decoration: none;
    }
    &> a.active{
        color: red;
    }
`
