import { styled } from "styled-components";
// import IconSend from "../../assets/icon-send.svg"
// import GooglePlay from "../../assets/GooglePlay.svg"
// import AppStore from "../../assets/AppStore.svg"

export const FooterCont = styled.div`
    padding: 80px 0 24px;
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
    background-color: #000;
`

export const FooterCreditsCont = styled.div`
    width: 1170px;
    display: flex;
    gap: 87px;
    margin-bottom: 60px;
`

export const FooterCreditsTextCont = styled.div`
    display: flex;
    flex-direction: column;
    gap: 16px;
`

export const FooterLogo = styled.p`
    font-family: 'Inter';
    font-size: 24px;
    font-style: normal;
    font-weight: 700;
    line-height: 24px;
    letter-spacing: 0.72px;
    color: #fafafa;
`

export const FooterCreditsTitle = styled.p`
    color: #fafafa;
    font-family: 'Poppins';
    font-size: 20px;
    font-style: normal;
    font-weight: 500;
    line-height: 28px;
    margin-bottom: 8px;
`

export const FooterCreditsTitle2 = styled.p`
    color: #fafafa;
    font-family: 'Poppins';
    font-size: 20px;
    font-style: normal;
    font-weight: 500;
    line-height: 28px;
    margin: 8px 0 8px;
`

export const FooterCreditsText = styled.p`
    font-family: 'Poppins';
    font-size: 16px;
    font-style: normal;
    font-weight: 400;
    line-height: 24px;
    color: #fafafa;
`

export const FooterCreditsText2 = styled.p`
    width: 175px;
    font-family: 'Poppins';
    font-size: 16px;
    font-style: normal;
    font-weight: 400;
    line-height: 24px;
    color: #fafafa;
`

export const FooterCreditsInpCont = styled.div`
    padding: 12px 16px;
    display: flex;
    gap: 32px;
    border-radius: 4px;
    border: 1.5px solid #FAFAFA;
`

export const FooterCreditsInp = styled.input`
    width: 130px;
    font-family: 'Poppins';
    font-size: 16px;
    font-style: normal;
    font-weight: 400;
    line-height: 24px;
    border: none;
    outline: none;
    background-color: transparent;
    color: white;

    ::placeholder{
        opacity: 0.4;
    }
`

export const FooterCreditsBtn = styled.button`
    border: none;
    background-color: transparent;
    width: 24px;
    height: 24px;
    /* background-image: url('${IconSend}'); */
    background-size: cover;
`

export const FooterCreditsBtnsMainCont = styled.div`
    display: flex;
    flex-direction: column;
    margin-bottom: 8px;
`

export const FooterCreditsTextdesc = styled.p`
    font-family: 'Poppins';
    color: #fafafa;
    opacity: 0.7;
    font-size: 12px;
    font-style: normal;
    font-weight: 500;
    line-height: 18px;
    margin-bottom: 12px;
`

export const FooterCreditsBtnsCont = styled.div`
    display: flex;
    align-items: center;
    gap: 10px;
`

export const FooterCreditsBtnCont = styled.div`
    display: flex;
    flex-direction: column;
    gap: 11px;
`

export const FooterCreditsBtnGP = styled.a`
    border: none;
    background-color: transparent;
    background-size: cover;
    /* background-image: url('${GooglePlay}'); */
    width: 104px;
    height: 37px;
`

export const FooterCreditsBtnAS = styled.a`
    border: none;
    background-color: transparent;
    background-size: cover;
    /* background-image: url('${AppStore}'); */
    width: 104px;
    height: 37px;
`

export const FooterCreditsIconsCont = styled.div`
    display: flex;
    gap: 24px;
`

export const FooterCreditsIcon = styled.a``

export const CopyRights = styled.p`
    color: #fafafa;
    display: flex;
    align-items: center;
    gap: 7.67px;
    opacity: 0.3;
